#include <iostream>\n#include <fstream>\n#include <sstream>\n#include <string>\n#include <map>\n\nint main() {\n    std::ifstream myfile("mydata.txt");\n    std::map<std::string, std::string> FastaMap;\n\n    std::cerr << "Read Fasta File to Map" << std::endl;\n\n    if (myfile.is_open()) {\n        std::string line;\n        while (std::getline(myfile, line)) {\n            // Check if the line is a header\n            if (line[0] == '>') {\n                std::string header = line.substr(1);  // Remove the '>' character\n                std::getline(myfile, line);  // Get the sequence on the next line\n                FastaMap[header] = line;   // Store in map\n            }\n        }\n        myfile.close();\n    } else {\n        std::cout << "Unable to open file";\n    }\n\n    // Print the contents of the map\n    for (const auto& it : FastaMap) {\n         std::cout << "Head: " << it.first << ", End: " << it.second << std::endl;\n    }\n}