// Assuming IMyComObject has both property accessors and direct property setters/getters\n\nIMyComObjectPtr comObjectPtr;\nHRESULT hr = CoCreateInstance(CLSID_MyComObject, NULL, CLSCTX_INPROC_SERVER,\n                              IID_IMyComObject, (void**)&comObjectPtr);\n\nif (SUCCEEDED(hr)) {\n    // Using Put/Get methods\n    comObjectPtr->PutValue(L"some value");\n    BSTR value;\n    comObjectPtr->GetValue(&value);\n    SysFreeString(value);  // Important to free the BSTR\n\n    // Using direct property access (if supported by the interface)\n    comObjectPtr->Value = L"another value";\n    BSTR anotherValue = comObjectPtr->Value;\n    SysFreeString(anotherValue);\n}