#include <pcap.h>\n#include <iostream>\n\nint main() {\n    char errBuf[PCAP_ERRBUF_SIZE];\n    pcap_t* handle = pcap_open_live("eth0", BUFSIZ, 1, 1000, errBuf);\n    \n    if (handle == nullptr) {\n        std::cerr << "Error opening interface: " << errBuf << std::endl;\n        return 1;\n    }\n\n    pcap_loop(handle, -1, [](u_char* args, const struct pcap_pkthdr* pkthdr, const u_char* packet) -> void {\n        // Process each packet here\n        std::cout << "Packet captured! Length: " << pkthdr->len << " bytes" << std::endl;\n    }, nullptr);\n\n    pcap_close(handle);\n    return 0;\n}