#include <atomic>\n#include <iostream>\n#include <thread>\n\nstd::atomic<int> counter(0);\n\nvoid increment() {\n    for (int i = 0; i < 100; ++i) {\n        counter.fetch_add(1, std::memory_order_relaxed);\n    }\n}\n\nint main() {\n    std::thread t1(increment);\n    std::thread t2(increment);\n\n    t1.join();\n    t2.join();\n\n    std::cout << "Final counter value: " << counter.load(std::memory_order_acquire) << std::endl;\n    return 0;\n}