#include <iostream>\n#include <fstream>\n#include <cstdio>\n\nint main() {\n    const char* path = "/dev/disk3";\n    FILE* f = fopen(path, "rb");\n    if (!f) {\n        perror("Failed to open device file");\n        return 1;\n    }\n\n    // Use fileno to get the file descriptor and wrap it with ifstream\n    int fd = fileno(f);\n    std::ifstream cf(fd);\n\n    char b;\n    cf.read(&b, 1);\n    if (cf.good()) {\n        std::cout << "Read success: " << static_cast<int>(b) << std::endl;\n    } else {\n        std::cerr << "Failed to read from device." << std::endl;\n    }\n\n    fclose(f); // Important: close the FILE* after use\n    return 0;\n}