// Example of using a DLL function in C++\n#include <windows.h>\n\ntypedef int (*MyFunction)(int, int);\n\nint main() {\n    HINSTANCE hDLL = LoadLibrary(TEXT("myLibrary.dll"));\n\n    if (hDLL != NULL) {\n        MyFunction myFunction = (MyFunction)GetProcAddress(hDLL, "MyFunctionName");\n\n        if (myFunction != NULL) {\n            int result = myFunction(5, 3);\n        } else {\n            FreeLibrary(hDLL);\n        }\n        FreeLibrary(hDLL);\n    }\n\n    return 0;\n}