#include <algorithm>\n#include <vector>\n\nstd::vector<MyType> minHeap;\n\nvoid addToHeap(MyType newItem) {\n    minHeap.push_back(newItem);\n    std::push_heap(minHeap.begin(), minHeap.end());\n}\n\n// Usage:\nfor (a very large set) {\n    addToHeap(newType);\n}\n\n// If you need to convert the heap back into a sorted vector at some point\nstd::sort_heap(minHeap.begin(), minHeap.end());