#include <algorithm>\n#include <vector>\n\nvoid insertSorted(std::vector<MyType>& vec, const MyType& newItem) {\n    auto it = std::lower_bound(vec.begin(), vec.end(), newItem);\n    vec.insert(it, newItem);\n}\n\n// Usage:\nvectorMyTypes.push_back(newType);  // Temporary addition\ninsertSorted(vectorMyTypes, newType);\nvectorMyTypes.pop_back();          // Remove temporary addition if needed