#include <windows.h>\n#include <wlanapi.h>\n#include <objbase.h>\n\nvoid main() {\n    HRESULT hr = CoInitializeEx(NULL, COINIT_MULTITHREADED);\n    if (FAILED(hr)) {\n        // Handle error\n    }\n\n    HANDLE clientHandle;\n    DWORD negotiatedVersion;\n    WlanOpenHandle(2, NULL, &negotiatedVersion, &clientHandle);\n\n    PWLAN_INTERFACE_INFO_LIST pIfList = NULL;\n    hr = WlanEnumInterfaces(clientHandle, NULL, &pIfList);\n    if (FAILED(hr)) {\n        // Handle error\n    }\n\n    for (DWORD i = 0; i < pIfList->dwNumberOfItems; i++) {\n        PWLAN_INTERFACE_INFO iface = &pIfList->InterfaceInfo[i];\n        PWLAN_AVAILABLE_NETWORK_LIST pBssList;\n        \n        hr = WlanGetAvailableNetworkList(clientHandle, &iface->InterfaceGuid, 0, NULL, &pBssList);\n        if (FAILED(hr)) {\n            // Handle error\n        }\n\n        for (DWORD j = 0; j < pBssList->dwNumberOfItems; j++) {\n            PWLAN_AVAILABLE_NETWORK network = &pBssList->Network[j];\n            wprintf(L"SSID: %s\n", network->dot11Ssid.ucSSID);\n            // ... process other information as needed ...\n        }\n\n        WlanFreeMemory(pBssList);\n    }\n\n    WlanFreeMemory(pIfList);\n    WlanCloseHandle(clientHandle, NULL);\n    CoUninitialize();\n}