#include <windows.h>\n#include <shlobj.h>\n\nvoid ExampleSHFileOperation() {\n    SHFILEOPSTRUCT sfo = { 0 };\n    TCHAR szFrom[MAX_PATH * 2] = _T("C:\\SourceFolder\\*");\n    TCHAR szTo[] = _T("D:\\DestinationFolder\\");\n\n    // Ensure strings are null-terminated and include double-null at the end\n    PathAddBackslash(szFrom);\n    lstrcat(szFrom, _T("*\0"));  // Double-null for termination\n\n    sfo.hwnd = NULL;   // No window handle\n    sfo.wFunc = FO_COPY;\n    sfo.pFrom = szFrom;\n    sfo.pTo = szTo;\n    sfo.fFlags = FOF_NOCONFIRMATION | FOF_SILENT | FOF_NOERRORUI;\n\n    int result = SHFileOperation(&sfo);\n    if (result != 0) {\n        // Handle error\n    }\n}