#include <windows.h>\n#include <wininet.h>\n#include <stdio.h>\n\n#pragma comment(lib, "WinINet.lib")\n\nint main() {\n    HINTERNET hSession = NULL, hConnect = NULL, hRequest = NULL;\n    BOOL bResults = FALSE;\n    DWORD dwSize = 0;\n\n    // Open an Internet session\n    hSession = InternetOpen("WinInetExample",\n                            INTERNET_OPEN_TYPE_DEFAULT,\n                            NULL,\n                            NULL,\n                            0);\n\n    if (hSession == NULL) {\n        printf("Failed to open internet session. Error: %d\n", GetLastError());\n        return 1;\n    }\n\n    // Connect to a server\n    hConnect = InternetConnect(hSession,\n                               "example.com",\n                               INTERNET_DEFAULT_HTTPS_PORT,\n                               NULL,\n                               NULL,\n                               INTERNET_SERVICE_HTTP,\n                               0,\n                               1);\n\n    if (hConnect == NULL) {\n        printf("Failed to connect. Error: %d\n", GetLastError());\n        InternetCloseHandle(hSession);\n        return 1;\n    }\n\n    // Open an HTTP request\n    hRequest = HttpOpenRequest(hConnect,\n                                "POST",\n                                "/your-post-endpoint",\n                                NULL,\n                                NULL,\n                                NULL,\n                                INTERNET_FLAG_SECURE,\n                                1);\n\n    if (hRequest == NULL) {\n        printf("Failed to open request. Error: %d\n", GetLastError());\n        InternetCloseHandle(hConnect);\n        InternetCloseHandle(hSession);\n        return 1;\n    }\n\n    // Send the HTTP POST request\n    const char *lpszHeaders = "Content-Type: application/x-www-form-urlencoded";\n    const char *szDataToSend = "param1=value1&param2=value2";\n\n    bResults = HttpSendRequest(hRequest,\n                               lpszHeaders,\n                               strlen(lpszHeaders),\n                               (LPVOID)szDataToSend,\n                               strlen(szDataToSend));\n\n    if (!bResults) {\n        printf("Failed to send request. Error: %d\n", GetLastError());\n        InternetCloseHandle(hRequest);\n        InternetCloseHandle(hConnect);\n        InternetCloseHandle(hSession);\n        return 1;\n    }\n\n    // Get the response\n    char szResponseBuffer[4096];\n    DWORD dwRead = 0;\n\n    do {\n        bResults = InternetReadFile(hRequest, szResponseBuffer, sizeof(szResponseBuffer) - 1, &dwRead);\n        if (!bResults || dwRead == 0) break;\n        szResponseBuffer[dwRead] = '\0';\n        printf("%s", szResponseBuffer);\n    } while (bResults && dwRead);\n\n    // Close the handles\n    InternetCloseHandle(hRequest);\n    InternetCloseHandle(hConnect);\n    InternetCloseHandle(hSession);\n\n    return 0;\n}