#include <SDL.h>\n#include <SDL_image.h>\n\nint main(int argc, char* argv[]) {\n    SDL_Init(SDL_INIT_VIDEO);\n    const char* image_path = "path_to_your_image.jpg"; // Replace with your path\n\n    SDL_Window* window = SDL_CreateWindow("Display Image", \n                                          SDL_WINDOWPOS_UNDEFINED, \n                                          SDL_WINDOWPOS_UNDEFINED, 640, 480, \n                                          SDL_WINDOW_SHOWN);\n\n    SDL_Surface* loaded_surface = IMG_Load(image_path);\n    if (loaded_surface == NULL) {\n        printf("Unable to load image %s! SDL_image Error: %s\n", image_path, IMG_GetError());\n        return -1;\n    }\n\n    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);\n    SDL_Surface* screen_surface = SDL_GetWindowSurface(window);\n\n    // Load image at specified path\n    SDL_Texture* texture = SDL_CreateTextureFromSurface(renderer, loaded_surface);\n    \n    SDL_RenderCopy(renderer, texture, NULL, NULL);\n    SDL_RenderPresent(renderer);\n\n    SDL_Delay(2000);  // Wait for 2 seconds\n\n    SDL_DestroyWindow(window);\n    IMG_Quit();\n    SDL_Quit();\n\n    return 0;\n}