#include <iostream>\n#include <string>\n\nint main() {\n    int myInt = 42;\n    std::string niceToSeeYouString = "nice to see you";\n    char someChar1 = '!';\n    double interestingDecimal = 3.14;\n    char someChar2 = '#';\n\n    std::string s = "Hello world, " + std::to_string(myInt) + " " + niceToSeeYouString \n                    + someChar1 + std::to_string(interestingDecimal) + someChar2;\n\n    std::cout << s << std::endl;\n\n    return 0;\n}