#include <shellapi.h>\n\nvoid reportResults() \n{\n    wstring env(_wgetenv(L"ProgramFiles"));\n    env += L"\\Internet Explorer\\iexplore.exe";\n    wstringstream url;\n    url << env.c_str() << L" http://yahoo.com";\n\n    // Use ShellExecute to open the URL\n    ShellExecute(\n        NULL,             // hwnd\n        L"open",           // Operation to perform (in this case, "open")\n        url.str().c_str(),// File/URL to execute/open\n        NULL,             // Parameters\n        NULL,             // Default directory\n        SW_SHOWNORMAL     // Show command\n    );\n}