#include <comdef.h>\n#import "your_server_library.tlb" // replace with your server tlb file\n\nHRESULT ConnectToSink(REFIID riid, IUnknown* pUnkClient, DWORD* pdwCookie)\n{\n    HRESULT hr = E_FAIL;\n    IConnectionPointContainer* pCPC = nullptr;  \n    hr = pUnkClient->QueryInterface(IID_PPV_ARGS(&pCPC));\n    if (SUCCEEDED(hr))\n    {\n        IConnectionPoint* pCP = nullptr;\n        hr = pCPC->FindConnectionPoint(riid, &pCP);\n        if (SUCCEEDED(hr))\n        {\n            hr = pCP->Advise(pUnkClient, pdwCookie); // Assuming pUnkClient is already the sink\n            pCP->Release();\n        }\n        pCPC->Release();\n    }\n\n    return hr;\n}