#include <iostream>\n#include <sstream>\n#include <string>\n\nint main() {\n    int myInt = 42;\n    std::string niceToSeeYouString = "nice to see you";\n    char someChar1 = '!';\n    double interestingDecimal = 3.14;\n    char someChar2 = '#';\n\n    std::ostringstream oss;\n    oss << "Hello world, " << myInt << " " << niceToSeeYouString \n        << someChar1 << interestingDecimal << someChar2;\n\n    std::string s = oss.str();\n    std::cout << s << std::endl;\n\n    return 0;\n}