#include <iostream>\n#include <fstream>\n#include <filesystem>\n\nint main() {\n    // Specify the name of the temporary file\n    const std::string tempFileName = "tmp.txt";\n\n    // Create an ofstream object to write to a new file\n    std::ofstream outFile(tempFileName);\n\n    if (!outFile) {\n        std::cerr << "Failed to create temp file: " << tempFileName << std::endl;\n        return 1; // Exit with error code\n    }\n\n    // Write some data into the file\n    outFile << "This is a temporary file." << std::endl;\n    outFile.close();\n\n    // Do other operations...\n\n    // When you're done, delete the file\n    try {\n        if (std::filesystem::remove(tempFileName)) {\n            std::cout << tempFileName << " was successfully deleted!" << std::endl;\n        } else {\n            std::cerr << "Failed to delete: " << tempFileName << std::endl;\n        }\n    } catch (const std::exception& e) {\n        std::cerr << "Caught an exception during file deletion: " << e.what() << std::endl;\n    }\n\n    return 0;\n}