#include <SQLiteCpp/SQLiteCpp.h>\n#include <iostream>\n\nint main() {\n    try {\n        // Open database (or create)\n        SQLite::Database db("example.db", SQLite::OPEN_READWRITE|SQLite::OPEN_CREATE);\n        \n        // Create a new table with an auto-increment integer primary key and text data\n        db.exec("CREATE TABLE IF NOT EXISTS test (id INTEGER PRIMARY KEY, name TEXT)");\n\n        // Insert a new row with text data\n        {\n            SQLite::Statement query(db, "INSERT INTO test (name) VALUES (?)");\n            query.bind(1, "example");\n            query.exec();\n        }\n        \n        // Query the data and print it to cout\n        SQLite::Statement query(db, "SELECT id, name FROM test ORDER BY id");\n        while (query.executeStep()) {\n            std::cout << "row(" << query.getColumn(0).getInt() << ", "\n                      << query.getColumn(1).getText() << ")" << std::endl;\n        }\n    } catch(const SQLite::Exception& e) {\n        std::cerr << "SQLite exception: " << e.what() << std::endl;\n        return 1;\n    }\n\n    return 0;\n}