#include <stdio.h>\n#include <stdlib.h>\n#include <string.h>\n\nint main() {\n    FILE *file = fopen("yourfile.txt", "r+");\n    if (!file) return 1;\n\n    char cLine[256];\n    std::string str;\n    \n    // Read all the data from the file.\n    while (fgets(cLine, sizeof(cLine), file)) {\n        str.append((std::string)cLine);\n    }\n\n    // Move the file position indicator to the beginning of the file\n    fseek(file, 0, SEEK_SET);\n\n    // Write the string back to the file\n    fputs(str.c_str(), file);\n\n    // Ensure all data is flushed to the file\n    fflush(file);\n\n    // Change the file's size to the length of the new data.\n    _chsize_s(_fileno(file), (long)str.size());\n    \n    fclose(file);\n    return 0;\n}