#include <windows.h>\n#include <winioctl.h>\n\n// This is a high-level, abstract conceptual example pseudocode\nvoid FindFileInDirectory(const char* directoryPath, const char* fileName) {\n    WIN32_FIND_DATAA fileData;\n    HANDLE hFind = FindFirstFileA(directoryPath, &fileData);\n\n    while (hFind != INVALID_HANDLE_VALUE && lstrcmpiA(fileData.cFileName, fileName) == 0) {\n        // Check if this is the file we are looking for\n        printf("Found: %s\n", fileData.cFileName);\n        FindClose(hFind);\n        return;\n    }\n    \n    hFind = FindFirstFileA(directoryPath, &fileData);\n    while (hFind != INVALID_HANDLE_VALUE && lstrcmpiA(fileData.cFileName, fileName) != 0) {\n        if ((fileData.dwFileAttributes & FILE_ATTRIBUTE_DIRECTORY) &&\n            strcmp(fileData.cFileName, ".") != 0 && strcmp(fileData.cFileName, "..") != 0) {\n\n            char subPath[MAX_PATH];\n            sprintf(subPath, "%s\\%s", directoryPath, fileData.cFileName);\n            FindFileInDirectory(subPath, fileName); // Recur into directory\n        }\n        \n        if (!FindNextFileA(hFind, &fileData)) {\n            break;\n        } \n    }\n\n    if (hFind != INVALID_HANDLE_VALUE) {\n        FindClose(hFind);\n    }\n}\n\nint main() {\n    FindFileInDirectory("E:\\documents", "test.txt");\n}