#include <windows.h>\n#include <stdio.h>\n\nvoid ListFilesInDirectory(LPCTSTR directory)\n{\n    WIN32_FIND_DATA findData;\n    HANDLE hFind;\n\n    // Create the search path\n    TCHAR searchPath[MAX_PATH];\n    StringCchCopy(searchPath, MAX_PATH, directory);\n    StringCchCat(searchPath, MAX_PATH, TEXT("\\*"));\n\n    // Start searching for files and directories\n    hFind = FindFirstFile(searchPath, &findData);\n    if (hFind == INVALID_HANDLE_VALUE)\n    {\n        printf("Error finding first file.\n");\n        return;\n    }\n\n    do\n    {\n        // Skip the "." and ".." entries\n        if (wcscmp(findData.cFileName, TEXT(".")) != 0 &&\n            wcscmp(findData.cFileName, TEXT("..")) != 0)\n        {\n            wprintf(L"%s\n", findData.cFileName);\n        }\n    } while (FindNextFile(hFind, &findData) != 0);\n\n    if (GetLastError() != ERROR_NO_MORE_FILES)\n    {\n        printf("Error finding next file.\n");\n    }\n\n    FindClose(hFind);\n}\n\nint main()\n{\n    ListFilesInDirectory(TEXT("SDMMC"));\n    return 0;\n}