#include <iostream>\n#include <windows.h>\n\nvoid PrintEnvironmentVariable(const char* variableName) {\n    const int bufferSize = 1024;\n    char buffer[bufferSize];\n    DWORD resultSize = GetEnvironmentVariableA(variableName, buffer, bufferSize);\n    if (resultSize > 0 && resultSize < bufferSize) {\n        std::cout << variableName << " = " << buffer << std::endl;\n    } else {\n        std::cerr << "Could not retrieve the environment variable: " << variableName << std::endl;\n    }\n}\n\nint main() {\n    PrintEnvironmentVariable("PATH");\n    return 0;\n}