#include "tinyxml.h"\n#include <stdio.h>\n\nint main() {\n    TiXmlDocument doc;\n    TiXmlElement *root = new TiXmlElement("Root");\n    TiXmlElement *child1 = new TiXmlElement("Child1");\n\n    child1->SetAttribute("attribute", "value");\n    child1->LinkEndChild(new TiXmlText("This is a text node."));\n\n    root->LinkEndChild(child1);\n    doc.LinkEndChild(root);\n\n    FILE* fp = fopen("output.xml", "w+");\n    if(fp == NULL) {\n        printf("Can't open file\n");\n        return 0;\n    }\n    doc.SaveFile(fp, true);\n    fclose(fp);\n\n    printf("XML saved to output.xml\n");\n\n    return 0;\n}