#include <windows.h>\n#include <lmaccess.h>\n\nNET_API_STATUS CreateUser(LPWSTR pszServer, LPWSTR pszUsername, LPWSTR pszPassword) {\n    USER_INFO_1 ui;\n    ui.usri1_name = pszUsername;\n    ui.usri1_password = pszPassword;\n    ui.usri1_priv = USER_PRIV_USER;\n    ui.usri1_home_dir = NULL;\n    ui.usri1_comment = NULL;\n    ui.usri1_flags = UF_SCRIPT | UF_NORMAL_ACCOUNT; // Ensure the account is a normal user account\n    ui.usri1_script_path = NULL;\n\n    NET_API_STATUS nStatus =\n        NetUserAdd(pszServer, 1, (LPBYTE)&ui, NULL);\n\n    return nStatus;\n}