#include <windows.h>\n#include <shlobj_core.h>\n\nvoid OpenUrlWithShellExecuteEx(LPCSTR url) {\n    SHELLEXECUTEINFO sei = { sizeof(sei), 0, NULL, "open", url, NULL, NULL, SW_SHOWNORMAL };\n    ShellExecuteEx(&sei);\n}\n\nvoid OpenUrlWithHlinkSimpleNavigateToString(LPCWSTR url) {\n    HRESULT hr;\n    IUniformResourceLocatorW* pURL;\n    \n    hr = CoCreateInstance(CLSID_CUrlHistory, NULL, CLSCTX_INPROC_SERVER, IID_IUniformResourceLocatorW, (LPVOID*)&pURL);\n    if (SUCCEEDED(hr)) {\n        hr = pURL->SetMonikerData(0, url);\n        if (SUCCEEDED(hr)) {\n            hr = HlinkSimpleNavigateToString(url, NULL, 0, NULL);\n        }\n        pURL->Release();\n    }\n}