// Pseudo-code example for Windows\nHMODULE handleA = LoadLibrary("A.dll");\nif (!handleA) {\n    // Handle error\n}\n\nHMODULE handleB = LoadLibrary("B.dll");\nif (!handleB) {\n    FreeLibrary(handleA);  // Clean up A before exiting\n    // Handle error\n}\n\n// Use functions from B\n...\n\nFreeLibrary(handleB);  // Decrement reference count for B\nFreeLibrary(handleA);  // Decrement reference count for A