#include <windows.h>\n#include <tlhelp32.h>\n\nDWORD GetParentProcessID(DWORD childProcID) {\n    HANDLE hSnapshot = CreateToolhelp32Snapshot(TH32CS_SNAPPROCESS, 0);\n    if (hSnapshot == INVALID_HANDLE_VALUE) {\n        return 0;\n    }\n\n    PROCESSENTRY32 pe;\n    pe.dwSize = sizeof(PROCESSENTRY32);\n    BOOL bOk;\n    DWORD parentPID = 0;\n\n    for (bOk = Process32First(hSnapshot, &pe); bOk; bOk = Process32Next(hSnapshot, &pe)) {\n        if (pe.th32ProcessID == childProcID) {\n            parentPID = pe.th32ParentProcessID;\n            break;\n        }\n    }\n\n    CloseHandle(hSnapshot);\n    return parentPID;\n}\n\nint main() {\n    DWORD childPid = 1234; // Replace with the PID of your child process\n    DWORD parentPid = GetParentProcessID(childPid);\n    printf("Parent Process ID: %d\n", parentPid);\n    return 0;\n}