#include <windows.h>\n#include <iostream>\n\ntypedef double (*CALCULATE_SINE)(double);\n\nint main() {\n    // Load the DLL\n    HMODULE hMod = LoadLibrary(TEXT("MathFunctions.dll"));\n    if (hMod == NULL) {\n        std::cerr << "Could not load DLL" << std::endl;\n        return 1;\n    }\n\n    // Get the address of the function in the DLL\n    CALCULATE_SINE calculateSine = (CALCULATE_SINE)GetProcAddress(hMod, "CalculateSine");\n    if (calculateSine == NULL) {\n        std::cerr << "Could not find function" << std::endl;\n        FreeLibrary(hMod);\n        return 1;\n    }\n\n    // Use the function\n    double result = calculateSine(1.0);\n    std::cout << "sin(1.0) = " << result << std::endl;\n\n    // Unload the DLL when done\n    FreeLibrary(hMod);\n    return 0;\n}