#include <iostream>\n#include <limits>  // For std::numeric_limits\n\nint main() {\n    int a;\n    bool validInput = false;\n\n    do {\n        std::cout << "\nEnter a number: ";\n        \n        if (std::cin >> a) {  // Try to extract an integer from the input\n            validInput = true;  // If extraction is successful, mark as valid input\n        } else {\n            // Extraction failed, clear the error flags and ignore invalid input\n            std::cout << "Invalid input. Please enter a valid number." << std::endl;\n            std::cin.clear();  // Clear the fail state\n            std::cin.ignore(std::numeric_limits<std::streamsize>::max(), '\n');  // Ignore the rest of the line\n        }\n    } while (!validInput);  // Repeat until we have a valid integer\n\n    return 0;\n}