#include <windows.h>\n#include <iostream>\n\nint main() {\n    // Find the main window of the MFC application\n    HWND hwnd = FindWindowA(NULL, "MFC Application Title");\n    \n    if (hwnd == NULL) {\n        std::cerr << "Failed to find the window." << std::endl;\n        return 1;\n    }\n\n    // Find the 'Update' button within the main window\n    HWND hButton = FindWindowExA(hwnd, NULL, "Button", "Update");\n\n    if (hButton == NULL) {\n        std::cerr << "Failed to find the button." << std::endl;\n        return 1;\n    }\n\n    // Simulate the clicking of the 'Update' button\n    SendMessage(hButton, BM_CLICK, 0, 0);\n\n    return 0;\n}