int value = 0;                            // variable to keep the actual value \nint ledPin1 = 9;                          // light connected to digital pin 9\nint ledPin2 = 10;                         // another light connected to digital pin 10\n\nunsigned long previousMillis = 0;         // will store last time LED was updated\nint fadeAmount = 5;                       // how many points to fade the LED by\nbool fadingIn = true;\n\nvoid setup() {\n  pinMode(ledPin1, OUTPUT);\n  pinMode(ledPin2, OUTPUT);\n  Serial.begin(9600);\n}\n\nvoid loop() {\n  unsigned long currentMillis = millis();\n\n  if (currentMillis - previousMillis >= 30) { // Adjust the delay between changes here\n    fadeLED();\n    previousMillis = currentMillis;\n  }\n}\n\nvoid fadeLED() {\n  // Change the brightness for next iteration of loop:\n  if (fadingIn == true) {\n    value += fadeAmount;       \n  } else if (fadingIn == false) {\n    value -= fadeAmount;        \n  }\n\n  // If complete, change direction\n  if (value >= 255) {               \n    fadingIn = false;\n  }\n  if (value <= 0) {                 \n    fadingIn = true;\n  }\n\n  // Set the brightness of ledPin:\n  analogWrite(ledPin1, value);\n  analogWrite(ledPin2, value);  // Add more LEDs as needed\n}