#include <windows.h>\n#include <iostream>\n\nint main() {\n    LPCSTR app = "c:\\path\\to\\java.exe"; // Use the full path to java.exe\n    LPCSTR args = "-cp c:\\dir\\updates.jar;c:\\dir\\main.jar Main";\n    \n    STARTUPINFO si = { sizeof(si) };\n    PROCESS_INFORMATION pi;\n\n    if (!CreateProcess(\n        app,\n        (LPSTR)args, // Command line arguments\n        NULL,       // Process handle not inheritable\n        NULL,       // Thread handle not inheritable\n        FALSE,      // Set handle inheritance to FALSE\n        0,          // No creation flags\n        NULL,       // Use parent's environment block\n        "c:\\dir",  // Optional: specify the working directory here\n        &si,\n        &pi)) {\n        std::cerr << "CreateProcess failed (" << GetLastError() << ").\n";\n        return -1;\n    }\n\n    // Wait until child process exits.\n    WaitForSingleObject(pi.hProcess, INFINITE);\n\n    // Close process and thread handles. \n    CloseHandle(pi.hProcess);\n    CloseHandle(pi.hThread);\n\n    return 0;\n}