#include <windows.h>\n#include <iostream>\n\nbool RegistryValueExists(const std::string& subKey, const std::string& valueName) {\n    HKEY hKey;\n    LONG result = RegOpenKeyEx(HKEY_LOCAL_MACHINE, subKey.c_str(), 0, KEY_READ, &hKey);\n    \n    if (result == ERROR_SUCCESS) {\n        BYTE buffer[1024];\n        DWORD bufferSize = sizeof(buffer);\n        \n        result = RegQueryValueEx(hKey, valueName.c_str(), NULL, NULL, buffer, &bufferSize);\n        \n        RegCloseKey(hKey);\n        \n        return result == ERROR_SUCCESS;\n    }\n    \n    return false;\n}\n\nint main() {\n    std::string subKey = "SOFTWARE\\Microsoft\\Windows";\n    std::string valueName = "CurrentVersion";\n\n    if (RegistryValueExists(subKey, valueName)) {\n        std::cout << "The registry value exists." << std::endl;\n    } else {\n        std::cout << "The registry value does not exist." << std::endl;\n    }\n\n    return 0;\n}