#include <windows.h>\n#include <objbase.h>\n#include <iostream>\n\nint main() {\n    HRESULT hr;\n    IUnknown* pUnk = nullptr;\n\n    // Initialize COM library for this thread.\n    hr = CoInitialize(nullptr);\n    if (FAILED(hr)) {\n        std::cerr << "Failed to initialize COM library. Error code: " << hr << std::endl;\n        return 1;  // Program has failed.\n    }\n\n    // Attempt to get a running instance of the object\n    // Replace "YourMonikerOrPath" with the appropriate moniker or path\n    const wchar_t* wszMoniker = L"YourMonikerOrPath";\n    \n    hr = CoGetObject(wszMoniker, nullptr, IID_PPV_ARGS(&pUnk));\n    if (SUCCEEDED(hr)) {\n        std::cout << "Successfully got a running instance of the object." << std::endl;\n        // Release the interface when done\n        pUnk->Release();\n    } else {\n        std::cerr << "Failed to get a running instance. Error code: " << hr << std::endl;\n    }\n\n    // Uninitialize COM library.\n    CoUninitialize();\n\n    return 0;\n}