// MyUnmanagedCppDll.cpp\n#include <windows.h>\n#include "MyManagedCppWrapper.i" // Ensure this is included for interop\nusing namespace MyManagedCppWrapper;\n\ntypedef WrapperClass* (*CreateObjectFunc)();\ntypedef void (*DeleteObjectFunc)(WrapperClass*);\ntypedef int (*AddMethod)(WrapperClass*, int, int);\n\nextern "C" __declspec(dllexport) int CallVBNetAdd(int a, int b) {\n    HMODULE hModule = LoadLibrary(TEXT("MyManagedCppWrapper.dll"));\n    if (!hModule) {\n        return -1;\n    }\n\n    CreateObjectFunc createObj = (CreateObjectFunc)GetProcAddress(hModule, "?CreateInstance@WrapperClass@MyManagedCppWrapper@@SAPAV12@XZ");\n    DeleteObjectFunc deleteObj = (DeleteObjectFunc)GetProcAddress(hModule, "?Destructor@WrapperClass@MyManagedCppWrapper@@QAEXXZ");\n    AddMethod addMethod = (AddMethod)GetProcAddress(hModule, "?Add@WrapperClass@MyManagedCppWrapper@@QAEHHH@Z");\n\n    if (!createObj || !deleteObj || !addMethod) {\n        FreeLibrary(hModule);\n        return -1;\n    }\n\n    WrapperClass* myObj = createObj();\n    int result = addMethod(myObj, a, b);\n    deleteObj(myObj);\n\n    FreeLibrary(hModule);\n    return result;\n}