#include <algorithm>\n#include <vector>\n#include <iostream>\n\nint main() {\n    std::vector<int> numbers = {1, 2, 3, 4, 5};\n    std::vector<int> result;\n\n    // Resize the destination vector to match the maximum possible size of the result.\n    result.resize(numbers.size());\n\n    auto end = std::copy_if(numbers.begin(), numbers.end(), result.begin(),\n                             [](int num) { return num % 2 == 0; });\n\n    // Erase the unused elements at the end of the result vector.\n    result.erase(end, result.end());\n\n    for (const auto& num : result) {\n        std::cout << num << " ";\n    }\n}