// Define pins connected to the shift register\nconst int dataPin = 2;\nconst int latchPin = 4;\nconst int clockPin = 5;\n\nvoid setup() {\n  // Set all control pins as outputs\n  pinMode(dataPin, OUTPUT);\n  pinMode(latchPin, OUTPUT);\n  pinMode(clockPin, OUTPUT);\n\n  // Initialize the display\n  shiftOutData(0); // Start with all LEDs off\n}\n\nvoid loop() {\n  // Example: Turn on LED connected to sink channel 1 (Blue 1)\n  byte data = B00000001; \n  shiftOutData(data);\n  delay(500);\n\n  // Example: Turn on LED connected to sink channel 2 (Green 1)\n  data = B00000010;\n  shiftOutData(data);\n  delay(500);\n\n  // Example: Turn on all Blue LEDs (Blue 1-8)\n  data = B11111111;\n  shiftOutData(data);\n  delay(500);\n\n  // Turn off all LEDs\n  data = B00000000;\n  shiftOutData(data);\n  delay(500);\n}\n\nvoid shiftOutData(byte data) {\n  // Send latch pin high to freeze the state of the register\n  digitalWrite(latchPin, LOW);\n\n  // Shift out the bits:\n  shiftOut(dataPin, clockPin, MSBFIRST, data);\n\n  // Send latch pin low to allow data to be changed\n  digitalWrite(latchPin, HIGH);\n}