#include <windows.h>\n#include <iostream>\n\nDWORD GetSystemIdleTime() {\n    LASTINPUTINFO lastInputInfo;\n    lastInputInfo.cbSize = sizeof(lastInputInfo);\n\n    if (!GetLastInputInfo(&lastInputInfo)) {\n        return 0; // Error retrieving the input info.\n    }\n\n    DWORD dwMilliseconds = GetTickCount() - lastInputInfo.dwTime;\n    return dwMilliseconds;\n}\n\nint main() {\n    DWORD idleTime = GetSystemIdleTime();\n    std::cout << "System Idle Time: " << idleTime / 1000.0 << " seconds\n";\n    return 0;\n}