#include <windows.h>\n#include <shellapi.h>\n\nint APIENTRY WinMain(HINSTANCE hInstance, HINSTANCE hPrevInstance, LPSTR lpCmdLine, int nCmdShow) {\n    // Path to your Java executable and the class containing main\n    const char* javaPath = "C:\\Path\\To\\java.exe";\n    const char* className = "-cp C:\\Path\\To\\YourJar.jar YourMainClass";\n\n    // Create a command line string\n    char cmdLine[1024];\n    snprintf(cmdLine, sizeof(cmdLine), "\"%s\" %s", javaPath, className);\n\n    // Execute the Java application\n    ShellExecute(NULL, "open", javaPath, cmdLine, NULL, SW_HIDE);\n\n    return 0;\n}