#include <string>\n\nstruct Multiplier {\n    int i;\n    int j;\n\n    Multiplier(int _i, int _j) : i(_i), j(_j) {}\n\n    // Conversion to int\n    operator int() const {\n        return i * j;\n    }\n\n    // Conversion to std::string\n    operator std::string() const {\n        return std::string(i, char('0' + j));\n    }\n};\n\ntemplate<typename T>\nT mul(int i, int j) {\n    Multiplier m(i, j);\n    return static_cast<T>(m);\n}\n\nint main() {\n    int n = mul<int>(6, 3); // n = 18\n    std::string s = mul<std::string>(6, 3); // s = "666"\n}