#include <iostream>\n#include <string>\n\nvoid printBitRepresentation(const std::string &s) {\n    for (char c : s) {\n        // Iterate over each bit, starting from MSB to LSB\n        for (int i = 7; i >= 0; --i) {\n            // Print the bit at position i\n            std::cout << ((c >> i) & 1);\n        }\n        std::cout << " "; // Space between bytes\n    }\n}\n\nint main() {\n    std::string s = "\x80";\n    printBitRepresentation(s);\n    return 0;\n}