#include <windows.h>\n\n// Example FILETIME (replace with your actual FILETIME value)\nFILETIME ft;\nft.dwLowDateTime = 0x15CAD14B; // Low part of the file time\nft.dwHighDateTime = 0x01D63AB2; // High part of the file time\n\n// Convert FILETIME to local FILETIME (this is usually not necessary here since we want UTC)\nFILETIME local_ft;\nFileTimeToLocalFileTime(&ft, &local_ft);\n\n// Convert FILETIME to SYSTEMTIME in UTC\nSYSTEMTIME st_utc;\nif (FileTimeToSystemTime(&ft, &st_utc)) {\n    // st_utc now contains the time in UTC without timezone and daylight savings adjustments\n    wprintf(L"UTC Time: %04d-%02d-%02d %02d:%02d:%02d.%03d\n",\n            st_utc.wYear, st_utc.wMonth, st_utc.wDay,\n            st_utc.wHour, st_utc.wMinute, st_utc.wSecond, st_utc.wMilliseconds);\n} else {\n    // Handle error\n}