#include <windows.h>\n#include <wtsapi32.h>\n#pragma comment(lib, "wtsapi32.lib")\n\nvoid LogonUser(const char* username, const char* password) {\n    HANDLE hToken = nullptr;\n    bool result = LogonUserA(\n        username,\n        "YourDomainOrMachineName",  // Change to your domain or machine name\n        password,\n        LOGON32_LOGON_INTERACTIVE,\n        LOGON32_PROVIDER_DEFAULT,\n        &hToken);\n\n    if (result) {\n        WTS_CONNECTSTATE_CLASS conState = WTSDisconnected;\n        DWORD sessionID = 0;\n\n        // Assuming you have the session ID, otherwise you need to enumerate sessions\n        if (WTSSetUserConfigA(WTS_CURRENT_SERVER_HANDLE,\n                                username,\n                                WTSInitialProgramUserConfig,\n                                nullptr,\n                                0)) {\n            // Now, try to log on to that session\n            result = WTSLogonUser(sessionID, &hToken);\n            if (!result) {\n                printf("Failed to switch user: %d\n", GetLastError());\n            }\n        } else {\n            printf("Failed to set config for user: %d\n", GetLastError());\n        }\n\n        CloseHandle(hToken);\n    } else {\n        printf("LogonUser failed: %d\n", GetLastError());\n    }\n}\n\nint main() {\n    LogonUser("OtherUserName", "Password");  // Replace with actual username and password\n    return 0;\n}