#include <windows.h>\n#include <initguid.h>\n\nGUID GenerateGuid()\n{\n    GUID guid;\n    HRESULT hr = CoCreateGuid(&guid);\n    \n    if (SUCCEEDED(hr))\n    {\n        return guid;\n    }\n    else\n    {\n        // Handle error, perhaps create a default GUID or throw an exception\n        memset(&guid, 0, sizeof(GUID));\n        return guid;\n    }\n}\n\nint main()\n{\n    GUID myGuid = GenerateGuid();\n    // Use your GUID here\n    return 0;\n}