#define N 70\nunsigned long long f_N(unsigned long long x) {\n    // Simple affine transformation combined with XOR and shifts\n    // This is not a secure encryption but provides the one-to-one mapping desired.\n    const unsigned long long A = 6364136223846793005ULL; // Large prime number for multiplication\n    const unsigned long long B = 1442695040888963407ULL; // Another large prime\n    x = A * x + B; // Linear transformation which is one-to-one\n    x ^= (x >> 10) ^ (x >> 20); // Simple mixing with XOR and shifts\n    return x;\n}\n\nunsigned long long inverse_f_N(unsigned long long y) {\n    // Inverse operations for the above function\n    y ^= (y >> 10) ^ (y >> 20); // Reverse of the mixing part would be complex, here we simplify assuming linearity.\n    const unsigned long long A_INV = 86470938563681033ULL; // Multiplicative inverse of A modulo 2^N\n    y = A_INV * (y - B); // Reverse linear transformation\n    return y;\n}