#include <QtWidgets>\n#include <QtSql>\n\nint main(int argc, char *argv[])\n{\n    QApplication app(argc, argv);\n\n    // Establish a connection to the database\n    QSqlDatabase db = QSqlDatabase::addDatabase("QSQLITE");\n    db.setDatabaseName("yourdatabase.db");\n\n    if (!db.open()) {\n        qDebug() << "Error: connection with database failed";\n        return 1;\n    }\n\n    // Create a model and select data from the table\n    QSqlTableModel *model = new QSqlTableModel();\n    model->setTable("your_table_name");\n    model->select();\n\n    // Create a view and set the model to it\n    QTableView *view = new QTableView;\n    view->setModel(model);\n\n    // Example of selecting a record: connect signal for selection changed\n    QObject::connect(view->selectionModel(), &QItemSelectionModel::currentChanged, [&](const QModelIndex &current, const QModelIndex &) {\n        if (current.isValid()) {\n            int row = current.row();\n            QSqlRecord record = model->record(row);\n            qDebug() << "Selected record:" << record;\n        }\n    });\n\n    view->show();\n\n    return app.exec();\n}