#include <QApplication>\n#include <QWebEngineView>\n#include <QFile>\n#include <QDebug>\n\nvoid capturePage(QWebEngineView *view, const QString &fileName) {\n    QFile file(fileName);\n    if (!file.open(QIODevice::WriteOnly | QIODevice::Text)) {\n        qDebug() << "Cannot open file for writing: " << fileName;\n        return;\n    }\n    QObject::connect(view, &QWebEngineView::loadFinished, [&]() {\n        QString html = view->page()->toHtml([](const QString& result) {\n            QFile file("output.html");\n            if (file.open(QIODevice::WriteOnly | QIODevice::Text)) {\n                QTextStream(&file) << result;\n            }\n            file.close();\n        });\n    });\n}\n\nint main(int argc, char *argv[]) {\n    QApplication app(argc, argv);\n\n    QWebEngineView view;\n    QObject::connect(&view, &QWebEngineView::loadProgress, [](int progress){\n        qDebug() << "Load Progress: " << progress << "%";\n    });\n\n    // Load the URL\n    view.load(QUrl("http://example.com"));\n\n    // Capture page after it is fully loaded\n    QObject::connect(&view, &QWebEngineView::loadFinished, [&]() {\n        capturePage(&view, "captured_page.html");\n    });\n\n    view.show();\n\n    return app.exec();\n}