#include <afxtempl.h>\n\nclass FixedSizeCList {\nprivate:\n    CTypedPtrList<CPtrList, void*> m_List;\n    int m_MaxSize;\n\npublic:\n    FixedSizeCList(int maxSize) : m_MaxSize(maxSize) {}\n\n    void AddTail(void* newElement) {\n        if (m_List.GetCount() >= m_MaxSize) {\n            // Remove the oldest element\n            if (!m_List.IsEmpty()) {\n                void* oldest = m_List.RemoveHead();\n                delete oldest; // Assuming elements are dynamically allocated, adjust as needed.\n            }\n        }\n        // Add new element to the tail\n        m_List.AddTail(newElement);\n    }\n\n    void DisplayList() {\n        POSITION pos = m_List.GetHeadPosition();\n        while (pos != NULL) {\n            void* element = m_List.GetNext(pos);\n            // Process each element, here we just print their addresses for demo.\n            TRACE(L"Element address: %p\n", element);\n        }\n    }\n};