#include <iostream>\n#include <cstring>\n#include <cctype>\n\nvoid SoundsAlike(const char input[], char scode[]) {\n    if (input[0] == '\0') { // Handle empty string case\n        std::strcpy(scode, "0000");\n        return;\n    }\n    \n    scode[0] = std::toupper(input[0]); // First character of the string is added to the code\n\n    int matchCount = 1;\n    int codeCount = 1;\n    while ((matchCount < std::strlen(input)) && (codeCount < 4)) {\n        char currentCode = '0';\n        switch (std::tolower(input[matchCount])) {\n            case 'b': case 'p': case 'v': case 'f': \n                currentCode = '1'; break;\n            case 'c': case 'g': case 'j': case 'k':\n            case 'q': case 's': case 'x': case 'z':\n                currentCode = '2'; break;\n            case 'd': case 't':\n                currentCode = '3'; break;\n            case 'l':\n                currentCode = '4'; break;\n            case 'm': case 'n':\n                currentCode = '5'; break;\n            case 'r':\n                currentCode = '6'; break;\n        }\n        \n        // Add to code if it's different from the last one\n        if (currentCode != '0' && scode[codeCount - 1] != currentCode) {\n            scode[codeCount++] = currentCode;\n        }\n        matchCount++;\n    }\n\n    while (codeCount < 4) {\n        scode[codeCount++] = '0';\n    }\n    scode[4] = '\0';\n\n    std::cout << scode << std::endl;\n}\n\nint main() {\n    char input[] = "Ray";\n    char scode[5];\n    SoundsAlike(input, scode);\n    return 0;\n}