// Example of using libxml2 in C++\n  #include <libxml/parser.h>\n  #include <libxml/xpath.h>\n\n  int main() {\n      xmlDocPtr doc = xmlReadFile("yourfile.xml", NULL, 0);\n      xmlXPathContextPtr xpathCtx = xmlXPathNewContext(doc);\n      xmlXPathObjectPtr result = xmlXPathEvalExpression((const xmlChar *)"//YourXPathQuery", xpathCtx);\n      // Process the XPath result\n      return 0;\n  }